<?php
namespace App\Http\Controllers; use App\System; use App\UserDomain; use Illuminate\Http\Request; class IndexController extends Controller { private $config = array(); function __construct() { $this->checkIsInMaintain(); $this->config = array('logo' => config('app.logo'), 'name' => config('app.name'), 'title' => config('app.title'), 'description' => config('app.description'), 'keywords' => config('app.keywords'), 'company' => config('app.company'), 'icp' => config('app.icp'), 'home_links' => System::_get('home_links'), 'js_tj' => System::_get('js_tj'), 'js_kf' => System::_get('js_kf')); } function docs() { return view('index.docs', array_merge($this->config, array())); } function docs_api() { return view('index.docs_api', array_merge($this->config, array())); } function help() { return view('index.help', array_merge($this->config, array())); } function about() { return view('index.about', array_merge($this->config, array())); } function legal_notice() { return view('index.legal_notice', array_merge($this->config, array())); } function forbid_product() { return view('index.forbid_product', array_merge($this->config, array())); } function tos() { return view('index.tos', array_merge($this->config, array())); } function privateAction() { return view('index.private', array_merge($this->config, array())); } }