<?php
 namespace Doctrine\DBAL\Driver; class PDOException extends \PDOException implements DriverException { private $errorCode; private $sqlState; public function __construct(\PDOException $exception) { parent::__construct($exception->getMessage(), 0, $exception); $this->code = $exception->getCode(); $this->errorInfo = $exception->errorInfo; $this->errorCode = isset($exception->errorInfo[1]) ? $exception->errorInfo[1] : $exception->getCode(); $this->sqlState = isset($exception->errorInfo[0]) ? $exception->errorInfo[0] : $exception->getCode(); } public function getErrorCode() { return $this->errorCode; } public function getSQLState() { return $this->sqlState; } } 