<?php
 namespace Illuminate\Mail\Transport; use Psr\Log\LoggerInterface; use Swift_Mime_SimpleMessage; use Swift_Mime_SimpleMimeEntity; class LogTransport extends Transport { protected $logger; public function __construct(LoggerInterface $logger) { $this->logger = $logger; } public function send(Swift_Mime_SimpleMessage $message, &$failedRecipients = null) { $this->beforeSendPerformed($message); $this->logger->debug($this->getMimeEntityString($message)); $this->sendPerformed($message); return $this->numberOfRecipients($message); } protected function getMimeEntityString(Swift_Mime_SimpleMimeEntity $entity) { $string = (string) $entity->getHeaders().PHP_EOL.$entity->getBody(); foreach ($entity->getChildren() as $children) { $string .= PHP_EOL.PHP_EOL.$this->getMimeEntityString($children); } return $string; } } 