<?php
 namespace Illuminate\Queue\Connectors; use Pheanstalk\Connection; use Pheanstalk\Pheanstalk; use Pheanstalk\PheanstalkInterface; use Illuminate\Queue\BeanstalkdQueue; class BeanstalkdConnector implements ConnectorInterface { public function connect(array $config) { $retryAfter = $config['retry_after'] ?? Pheanstalk::DEFAULT_TTR; return new BeanstalkdQueue($this->pheanstalk($config), $config['queue'], $retryAfter); } protected function pheanstalk(array $config) { return new Pheanstalk( $config['host'], $config['port'] ?? PheanstalkInterface::DEFAULT_PORT, $config['timeout'] ?? Connection::DEFAULT_CONNECT_TIMEOUT, $config['persistent'] ?? false ); } } 