<?php
 namespace Illuminate\Routing; class ControllerMiddlewareOptions { protected $options; public function __construct(array &$options) { $this->options = &$options; } public function only($methods) { $this->options['only'] = is_array($methods) ? $methods : func_get_args(); return $this; } public function except($methods) { $this->options['except'] = is_array($methods) ? $methods : func_get_args(); return $this; } } 