<?php
 namespace Predis\Response\Iterator; use Predis\Connection\NodeConnectionInterface; class MultiBulk extends MultiBulkIterator { private $connection; public function __construct(NodeConnectionInterface $connection, $size) { $this->connection = $connection; $this->size = $size; $this->position = 0; $this->current = $size > 0 ? $this->getValue() : null; } public function __destruct() { $this->drop(true); } public function drop($disconnect = false) { if ($disconnect) { if ($this->valid()) { $this->position = $this->size; $this->connection->disconnect(); } } else { while ($this->valid()) { $this->next(); } } } protected function getValue() { return $this->connection->read(); } } 