 namespace Symfony\Component\CssSelector\Tests\Parser\Shortcut; use PHPUnit\Framework\TestCase; use Symfony\Component\CssSelector\Node\SelectorNode; use Symfony\Component\CssSelector\Parser\Shortcut\ClassParser; class ClassParserTest extends TestCase { public function testParse($source, $representation) { $parser = new ClassParser(); $selectors = $parser->parse($source); $this->assertCount(1, $selectors); $selector = $selectors[0]; $this->assertEquals($representation, (string) $selector->getTree()); } public function getParseTestData() { return array( array('.testclass', 'Class[Element[*].testclass]'), array('testel.testclass', 'Class[Element[testel].testclass]'), array('testns|.testclass', 'Class[Element[testns|*].testclass]'), array('testns|*.testclass', 'Class[Element[testns|*].testclass]'), array('testns|testel.testclass', 'Class[Element[testns|testel].testclass]'), ); } } 